import{_ as e,c as r,o as t,a as o}from"./app.551f901f.js";const v='{"title":"\u6982\u8FF0","description":"","frontmatter":{"sidebarDepth":4},"headers":[{"level":2,"title":"Developing for AR","slug":"developing-for-ar"},{"level":2,"title":"\u6784\u5EFA AR \u4F53\u9A8C","slug":"\u6784\u5EFA-ar-\u4F53\u9A8C"},{"level":3,"title":"AR \u7EC4\u4EF6","slug":"ar-\u7EC4\u4EF6"},{"level":3,"title":"AR \u7279\u6027","slug":"ar-\u7279\u6027"}],"relativePath":"ar/overview.md","lastUpdated":1638858231801}',d={},a=o('<h1 id="\u6982\u8FF0" tabindex="-1">\u6982\u8FF0 <a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a></h1><blockquote><p><strong>AR \u529F\u80FD\u4EC5\u53EF\u7528\u4E8E\u652F\u6301 ARKit \u548C ARCore \u7684\u624B\u673A\u8BBE\u5907</strong> &gt; <a href="https://developer.apple.com/library/content/documentation/DeviceInformation/Reference/iOSDeviceCompatibility/DeviceCompatibilityMatrix/DeviceCompatibilityMatrix.html" target="_blank" rel="noopener noreferrer">ARKit \u652F\u6301\u7684\u8BBE\u5907</a> &gt; <a href="https://developers.google.com/ar/discover/supported-devices" target="_blank" rel="noopener noreferrer">ARCore \u652F\u6301\u7684\u8BBE\u5907</a></p></blockquote><p>Viro \u901A\u8FC7\u5404\u79CD AR \u7279\u5B9A\u7EC4\u4EF6\u548C\u529F\u80FD\u652F\u6301\u589E\u5F3A\u73B0\u5B9E (AR) \u5F00\u53D1\u3002\u672C\u6307\u5357\u5C06\u9996\u5148\u6982\u8FF0 AR\uFF0C\u5E76\u63D0\u4F9B\u4F7F\u5F00\u53D1\u4EBA\u5458\u80FD\u591F\u6784\u5EFA AR \u4F53\u9A8C\u7684\u7EC4\u4EF6\u548C\u529F\u80FD\u7684\u8FDB\u9636\u6982\u8FF0\u3002</p><h2 id="developing-for-ar" tabindex="-1">Developing for AR <a class="header-anchor" href="#developing-for-ar" aria-hidden="true">#</a></h2><blockquote><p>viro \u6587\u6863\u6682\u65E0\u5185\u5BB9</p></blockquote><h2 id="\u6784\u5EFA-ar-\u4F53\u9A8C" tabindex="-1">\u6784\u5EFA AR \u4F53\u9A8C <a class="header-anchor" href="#\u6784\u5EFA-ar-\u4F53\u9A8C" aria-hidden="true">#</a></h2><p>Viro \u5E73\u53F0\u63D0\u4F9B\u4E86\u5927\u91CF\u7EC4\u4EF6\uFF0C\u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u5229\u7528\u8FD9\u4E9B\u7EC4\u4EF6\u6765\u6784\u5EFA AR \u4F53\u9A8C\u3002\u7136\u800C\uFF0C\u4E0E\u4F20\u7EDF\u7684 3D \u6E32\u67D3\u548C VR \u4E0D\u540C\uFF0CAR \u4F53\u9A8C\u65E8\u5728\u54CD\u5E94\u7528\u6237\u7684\u771F\u5B9E\u4E16\u754C\u3002\u4E3A\u4E86\u5B9E\u73B0\u8FD9\u4E00\u70B9\uFF0CViro \u5E73\u53F0\u63D0\u4F9B\u4E86\u4E00\u4E9B\u7279\u5B9A\u4E8E AR \u7684\u7EC4\u4EF6\u548C\u529F\u80FD\uFF0C\u8BE6\u60C5\u5982\u4E0B\uFF1A</p><h3 id="ar-\u7EC4\u4EF6" tabindex="-1">AR \u7EC4\u4EF6 <a class="header-anchor" href="#ar-\u7EC4\u4EF6" aria-hidden="true">#</a></h3><p>\u8FD9\u4E9B\u662F Viro \u7684 \u7279\u610F\u4E3A AR \u6784\u5EFA\uFF0C\u5E76\u4EC5\u7528\u4E8E AR \u7684\u7EC4\u4EF6\u3002</p><table><thead><tr><th>\u7EC4\u4EF6</th><th>\u63CF\u8FF0</th></tr></thead><tbody><tr><td><a href="https://docs.viromedia.com/docs/viroarscenenavigator" target="_blank" rel="noopener noreferrer"><code>ViroARSceneNavifator</code></a></td><td>top \u7EA7 RN \u7EC4\u4EF6\uFF0C\u5448\u73B0 ViroARScenes \u89C6\u56FE</td></tr><tr><td><a href="https://docs.viromedia.com/docs/viroarscenenavigator" target="_blank" rel="noopener noreferrer"><code>ViroARScene</code></a></td><td>\u5448\u73B0\u4E00\u6BB5 AR \u4F53\u9A8C\u5FC5\u8981\u7684 AR \u7EC4\u4EF6\u7684\u903B\u8F91\u5BB9\u5668\uFF0C\u7EF4\u62A4\u5728\u73B0\u5B9E\u4E16\u754C\u4E2D\u4EE5 AR \u6E32\u67D3\u7684\u573A\u666F\u56FE\u3002\u82E5\u9700\u8981\u591A\u79CD AR \u4F53\u9A8C\uFF0C\u53EF\u4F7F\u7528\u591A\u4E2A <code>ViroARScene</code></td></tr><tr><td><a href="https://docs.viromedia.com/docs/viroarplane" target="_blank" rel="noopener noreferrer"><code>ViroARPlane</code></a></td><td>\u4E00\u4E2A\u7EC4\u4EF6\uFF0C\u53EF\u4EE5\u5141\u8BB8\u5E76\u81EA\u52A8\u7BA1\u7406 ViroReact \u7EC4\u4EF6\uFF08 \u6709\u5173\u4E8E AR \u7CFB\u7EDF\u53D1\u73B0\u7684\u5E73\u9762\uFF09\u7684\u5B9A\u4F4D\u3002 \u53EF\u4EE5\u662F\u684C\u9762\u6E38\u620F\u7684\u8868\u9762\uFF0C\u4E5F\u53EF\u4EE5\u662F\u5B89\u88C5\u865A\u62DF\u56FE\u7247\u7684\u5899\u58C1\u3002</td></tr><tr><td><a href="https://docs.viromedia.com/docs/viroarplaneselector" target="_blank" rel="noopener noreferrer"><code>ViroARPlaneSelector</code></a></td><td>\u5305\u542B <code>ViroARPlane</code> \u7684\u4E00\u4E2A\u4FBF\u5229\u7EC4\u4EF6\uFF0C\u5141\u8BB8\u6700\u7EC8\u7528\u6237\u9009\u62E9\u5E0C\u671B\u5F00\u53D1\u4EBA\u5458\u7528\u6765\u663E\u793A\u5185\u5BB9\u7684\u5E73\u9762\u3002</td></tr><tr><td><a href="https://docs.viromedia.com/docs/viroarplaneselector" target="_blank" rel="noopener noreferrer"><code>ViroARImageMarker</code></a></td><td>\u4E00\u4E2A AR \u7CFB\u7EDF\u68C0\u6D4B\u5230\u7ED9\u5B9A\u56FE\u50CF\uFF0C\u5F00\u53D1\u4EBA\u5458\u80FD\u591F\u76F8\u5BF9\u4E8E\u5176\u653E\u7F6E\u5BF9\u8C61\u7684\u7EC4\u4EF6\u3002\u53EF\u4EE5\u521B\u5EFA\u51FA\u73B0\u5728\u771F\u5B9E\u4E16\u754C\u56FE\u50CF\u4E0A\u7684\u6574\u4E2A\u865A\u62DF\u7528\u6237\u754C\u9762\u3002\u6216\u8005\u8BA9\u7535\u5F71\u6D77\u62A5\u4E4B\u7C7B\u7684\u7269\u4F53\u5728\u88AB\u68C0\u6D4B\u5230\u65F6\u53D8\u5F97\u751F\u52A8\u8D77\u6765\u3002</td></tr><tr><td><a href="https://docs.viromedia.com/docs/viroartrackingtargets" target="_blank" rel="noopener noreferrer"><code>ViroARTrackingTargets</code></a></td><td>\u5141\u8BB8\u7528\u6237\u521B\u5EFA\u4E0E <code>ViroARImageMarker</code> \u4E00\u8D77\u4F7F\u7528\u7684\u76EE\u6807\u7EC4\u4EF6\u3002 API \u7C7B\u4F3C\u4E8E\u6750\u6599\u3002</td></tr></tbody></table><p>\u5982\u4F60\u6240\u89C1\uFF0C<code>ViroARSceneNavigator</code> \u548C <code>ViroARScene</code> \u66FF\u6362\u4E86 VR \u7684 <code>ViroSceneNavigator</code> \u548C <code>ViroScene</code>\uFF0C\u5E76\u6DFB\u52A0\u4E86\u66F4\u591A\u7279\u5B9A\u4E8E AR \u7684\u65B9\u6CD5\u548C\u5C5E\u6027\u3002</p><p><code>ViroARPlane</code> \u548C <code>ViroARPlaneSelector</code> \u662F\u53E6\u5916\u4E24\u4E2A\u4EC5\u7528\u4E8E AR \u7684\u7EC4\u4EF6\uFF0C\u5B83\u4EEC\u5141\u8BB8\u5F00\u53D1\u4EBA\u5458\u76F8\u5BF9\u4E8E\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u5E73\u9762\u653E\u7F6E\u5185\u5BB9\u3002</p><h3 id="ar-\u7279\u6027" tabindex="-1">AR \u7279\u6027 <a class="header-anchor" href="#ar-\u7279\u6027" aria-hidden="true">#</a></h3><p>AR \u7CFB\u7EDF\u8FD8\u63D0\u4F9B\u4E00\u4E9B\u5176\u5B83\u4FE1\u606F\uFF0C\u901A\u8FC7\u73B0\u6709 AR \u7EC4\u4EF6\u7684\u4EE5\u4E0B\u529F\u80FD\u63D0\u4F9B\uFF1A</p><table><thead><tr><th>\u7279\u6027</th><th>API \u4F4D\u7F6E</th><th>\u63CF\u8FF0</th></tr></thead><tbody><tr><td>6 \u5EA6\u76F8\u673A\u79FB\u52A8</td><td>\u53EF\u4EE5\u901A\u8FC7 <code>ViroARScene</code> \u4E2D\u7684 <code>getCameraOrientationAsync()</code> \u8BBF\u95EE\u76F8\u673A\u65B9\u5411\u3002</td><td>(\u865A\u62DF)\u76F8\u673A\u4F1A\u968F\u7740\u7528\u6237\u5728\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u79FB\u52A8\u800C\u81EA\u52A8\u79FB\u52A8\u3002\u8FD9\u5BFC\u81F4\u865A\u62DF\u5BF9\u8C61\u770B\u8D77\u6765\u505C\u7559\u5728\u5B83\u4EEC\u7684\u4F4D\u7F6E\u3002</td></tr><tr><td>\u6444\u50CF\u76F4\u901A\uFF08\u5448\u73B0\u4E3A\u80CC\u666F\uFF09</td><td>\u81EA\u52A8\u52A0\u8F7D</td><td>\u5728 AR \u4E2D\uFF0C\u540E\u7F6E\u6444\u50CF\u5934\u88AB\u542F\u7528\u5E76\u7528\u4F5C\u6E32\u67D3\u865A\u62DF\u5BF9\u8C61\u7684\u89C6\u56FE\u7684\u201C\u80CC\u666F\u201D\u3002</td></tr><tr><td>\u73AF\u5883\u5149\u4F30\u8BA1</td><td><code>ViroARScene</code> \u4E2D\u7684 <code>onAmbientLightUpdate</code></td><td>\u63D0\u4F9B\u76F8\u673A\u68C0\u6D4B\u5230\u7684\u5149\u5F3A\u5EA6\u548C\u8272\u6E29\u7684\u4F30\u8BA1\u503C\u3002</td></tr><tr><td><code>FixedToWorld</code> \u548C <code>FixedToPlane</code> \u62D6\u52A8</td><td>\u6240\u6709\u201C\u53EF\u62D6\u52A8\u201D\u7EC4\u4EF6\u90FD\u901A\u8FC7 dragType \u5C5E\u6027\u652F\u6301\u3002</td><td>\u5141\u8BB8\u7528\u6237\u62D6\u52A8\u5BF9\u8C61\uFF0C\u4F7F\u5B83\u4EEC\u770B\u8D77\u6765\u56FA\u5B9A\u5728\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u70B9\u4E0A\u3002</td></tr><tr><td>AR-based Hit Tests</td><td><a href="https://docs.viromedia.com/docs/viroarscene" target="_blank" rel="noopener noreferrer"><code>ViroARScene</code></a> \u4E2D\u7684\u65B9\u6CD5</td><td>\u5141\u8BB8\u7528\u6237\u4ECE AR \u7CFB\u7EDF\u4E2D\u83B7\u53D6\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u4F4D\u7F6E\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u7528\u6237\u89E6\u6478\u5C4F\u5E55\uFF0C\u5979\u89E6\u6478\u4E86\u54EA\u4E2A\u73B0\u5B9E\u4E16\u754C\u7684\u5BF9\u8C61\uFF08\u5982\u679C\u6709\uFF09\uFF1F</td></tr><tr><td>\u4F20\u9001\u95E8</td><td><a href="https://docs.viromedia.com/docs/viroportalscene" target="_blank" rel="noopener noreferrer"><code>ViroPortalScene</code></a> \u548C <a href="https://docs.viromedia.com/docs/viroportal" target="_blank" rel="noopener noreferrer"><code>ViroPortal</code></a> \u7EC4\u4EF6</td><td>\u5141\u8BB8\u5F00\u53D1\u4EBA\u5458\u5C06\u865A\u62DF\u201C\u95E8\u6237\u201D\u4ECE\u73B0\u5B9E\u4E16\u754C\u6DFB\u52A0\u5230\u865A\u62DF\u4E16\u754C\uFF0C\u7136\u540E\u518D\u8FD4\u56DE\u3002</td></tr><tr><td>\u540E\u671F\u5904\u7406\u6548\u679C</td><td><code>ViroARScene</code> \u4E2D\u7684 <code>postProcessEffects</code> \u5C5E\u6027</td><td>\u5141\u8BB8\u5F00\u53D1\u4EBA\u5458\u7ED9\u573A\u666F\u5E94\u7528\u9884\u5148\u6784\u5EFA\u7684\u540E\u671F\u5904\u7406\u6548\u679C\u3002</td></tr><tr><td>\u89C6\u9891\u548C\u9759\u6001\u6355\u83B7</td><td>ViroARSceneNavigator \u4E2D\u7684 Recording API</td><td>\u5141\u8BB8\u5F00\u53D1\u4EBA\u5458\u5728\u8BB0\u5F55\u7684\u6444\u50CF\u673A\u89C6\u56FE\u4E4B\u4E0A\u8F7B\u677E\u8BB0\u5F55\u865A\u62DF\u5BF9\u8C61\u3002</td></tr></tbody></table><p>\u501F\u52A9\u4E0A\u8FF0\u529F\u80FD\uFF0C\u5F00\u53D1\u4EBA\u5458\u53EF\u4EE5\u4F7F\u7528\u66F4\u903C\u771F\u7684\u7167\u660E\u6765\u7167\u4EAE\u5176 AR \u4F53\u9A8C\uFF0C\u4F7F\u865A\u62DF\u5BF9\u8C61\u4E0E\u73AF\u5883\u8FDB\u884C\u903C\u771F\u7684\u4EA4\u4E92\uFF0C\u6DFB\u52A0\u5F02\u754C\u95E8\u7B49\u7B49\u3002</p>',16),c=[a];function i(n,l,s,p,h,A){return t(),r("div",null,c)}var f=e(d,[["render",i]]);export{v as __pageData,f as default};
